import React, { Component, createContext } from 'react';

import { Link } from 'react-router-dom';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import classNames from 'classnames';

import styled from 'styled-components';

import { IconButton } from '@material-ui/core';
import Badge from '@material-ui/core/Badge';
import MenuIcon from '@material-ui/icons/Menu';
import ForumIcon from '@material-ui/icons/Forum';
import NotificationsIcon from '@material-ui/icons/Notifications';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import MailIcon from '@material-ui/icons/Mail';

import { Content, Profile } from './styles';

export const Container = styled.div`
  background: ${props => props.theme.background}
  padding: 0 10px;
`;

const ThemeContext = createContext('light');

const styles = theme => ({
  root: {
    color: '#fff',
  },
  menu: {
    color: '#fff',
  },
});

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menuOn: false,
    };

    // Aqui utilizamos o `bind` para que o `this` funcione dentro da nossa callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      menuOn: !state.menuOn,
    }));
  }

  render() {
    const { classes } = this.props;
    const { menuOn } = this.state;
    console.log(this.props.theme);
    return (
      <ThemeContext.Consumer>
        {theme => (
          <Container className={classes.header}>
            <Content>
              <nav>
                <IconButton
                  color="inherit"
                  aria-label="Open drawer"
                  onClick={this.handleClick}
                  className={classNames(classes.menuButton)}
                >
                  {menuOn ? (
                    <MenuIcon className={classes.menu} />
                  ) : (
                    <ChevronLeftIcon className={classes.menu} />
                  )}
                </IconButton>
                <div>
                  <Link to="/dashboard">DASHBOARD</Link>
                </div>
              </nav>
              <aside>
                <div>
                  <IconButton aria-label="show 12 new mails" color="inherit">
                    <Badge badgeContent={12} color="secondary">
                      <MailIcon className={classes.root} />
                    </Badge>
                  </IconButton>
                  <IconButton aria-label="show 4 new talk" color="inherit">
                    <Badge badgeContent={4} color="secondary">
                      <ForumIcon className={{ color: 'red' }} />
                    </Badge>
                  </IconButton>
                  <IconButton
                    aria-label="show 17 new notifications"
                    color="inherit"
                  >
                    <Badge badgeContent={17} color="secondary">
                      <NotificationsIcon className={{}} />
                    </Badge>
                  </IconButton>
                </div>
                <Profile>
                  <div>
                    <strong>Paulo Spiguel</strong>
                    <Link to="/profile">Meu Perfil</Link>
                  </div>
                  <img
                    src="https://api.adorable.io/avatars/50/abott@adorable.pngCopy to Clipboard
  "
                    alt="Imagem Perfil"
                  />
                </Profile>
              </aside>
            </Content>
          </Container>
        )}
      </ThemeContext.Consumer>
    );
  }
}

Header.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(Header);
