import styled, { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
  *{
    margin: 0;
    padding: 0;
    outline: 0;
    box-sizing: border-box
  }

  html, body, #root{
    /*min-height: 100vh;*/
  }

  body{
    /*background: #7159c1;*/
    -webkit-font-smoothing: antialiased !important;
  }

  body, input, button {
    color: #222;
    font-size: 14px;
    font-family: Arial, Helvetica, sans-serif;
  }

  button{
    cursor: pointer;
  }

  #wapper{}
  #wapper-aside{}
  #wapper-main{}
  `;

export const Wrapper = styled.div`
  /*width: 100%;*/
  display: grid;
  grid-template-areas: 10% 90%;
  grid-template-rows: 8vh 82vh 10vh;
  grid-template-areas:
    'header header'
    'aside main'
    'footer footer';

  font-style: normal;
  font-weight: 200;

  /*
  font-family: Roboto, sans-serif;
  align-items: stretch;
  */
`;

export const ContentWrapper = styled.section`
  /*
  width: 100%;
  */
`;
