import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import { Divider } from '@material-ui/core';
import ThemeSwitcher from '../ThemeSwitcher';

// eslint-disable-next-line react/prefer-stateless-function
class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      drawerWidth: 240,
      drawerWidthDefault: 140,
    };
  }

  render() {
    return (
      <Drawer>
        <Divider />
        <div className={{}}>
          {/* <Link className={classes.logoLink} to="/"> */}
          <img
            alt="Brainalytica logo"
            className={{}}
            src="https://upload.wikimedia.org/wikipedia/en/0/02/Meo_logo.svg"
          />
          {/* </Link> */}
        </div>
        <Divider className={classes.logoDivider} />
        <ThemeSwitcher toggleTheme={this.props.toggleTheme} />
        <Divider />
        <List>
          {['Dashboard', 'Employees', 'Sales', 'Inventory', 'Settings'].map(
            (text, index) => (
              <ListItem
                button
                key={text}
                component={NavLink}
                selected={`/${text.toLowerCase()}` === this.props.params}
                to={`/${text.toLowerCase()}`}
              >
                <ListItemIcon
                  className={classNames({
                    [classes.navIcon]: true,
                    [classes.marginAuto]: !this.props.open,
                    [classes.activeItem]:
                      `/${text.toLowerCase()}` === this.props.params,
                  })}
                >
                  {/* {text === 'Dashboard' ? (
                    <DashboardIcon />
                  ) : text === 'Sales' ? (
                    <ChartIcon />
                  ) : text === 'Employees' ? (
                    <UsersIcon />
                  ) : text === 'Inventory' ? (
                    <InventoryIcon />
                  ) : text === 'Settings' ? (
                    <Settings />
                  ) : (
                    console.log('Missing Icon')
                  )} */}
                </ListItemIcon>
                <ListItemText
                  primary={
                    <Typography
                      type="body2"
                      style={{ color: '#8c939d' }}
                      className={classNames({
                        [classes.activeItem]:
                          `/${text.toLowerCase()}` === this.props.params,
                      })}
                    >
                      {text}
                    </Typography>
                  }
                  className={classNames({
                    [classes.hide]: !this.props.open,
                  })}
                />
              </ListItem>
            )
          )}
        </List>
        <Divider />
      </Drawer>
    );
  }
}

Sidebar.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(Sidebar);
