import React from 'react';
import { makeStyles } from '@material-ui/styles';
import { hidden } from 'ansi-colors';
import { Container } from './styles';

import ListMenu from './ListMenu';
import DeepMenu from './DeepMenu';
import SmallDeepMenu from './SmallDeepMenu';
import Logo from './Logo';

const useStyles = makeStyles({
  root: { background: 'red' },
  logo: {
    display: 'none',
  },
});

const hendleScreen = () => {
  const ecreen = document.body.clientWidth;

  return console.log(ecreen > 468 ? <DeepMenu /> : <SmallDeepMenu />);
};

export default function Sidebar() {
  const classes = useStyles();

  return (
    <>
      <Container>
        <div className={classes.logo}>
          <Logo />
        </div>
        <ListMenu />
        {window.addEventListener('resize', hendleScreen)}
      </Container>
    </>
  );
}
