import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

// import "../styles/Sidenav.css";
import SidenavLink from '../SidenavLink';
import SidenavDropdown from '../SidenavDropdown';

import logo from '../../assets/logo.svg'; // 'react-logo.png';

const Nav = styled.nav`
  min-height: 100vh;
  min-width: 150px;
  max-width: 150px;
  overflow-y: auto;
  background: rgba(0, 0, 0, 0.6);
  color: #fff;
  -webkit-transition: all 0.5s ease;
  -moz-transition: all 0.5s ease;
  -o-transition: all 0.5s ease;
  transition: all 0.5s ease;
  &.active {
    margin-left: -150px;
  }
  @media (max-width: 768px) {
    margin-left: -150px;
  }
`;

const BrandDiv = styled.div`
  background: rgba(0, 0, 0, 0.6);
`;

const UnorderedList = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  @media (max-width: 768px) {
    margin-left: -250px;
    &.active {
      margin-left: 0;
    }
  }
`;

const ListHeading = styled.p`
  padding: 10px;
`;

const LogoImage = styled.img`
  width: 128px;
  height: 128px;
`;

const Sidenav = props => {
  const { pages } = props;

  return (
    <Nav id="sidenav-wrapper" className="">
      <BrandDiv>
        <LogoImage src={logo} alt="logo" />
      </BrandDiv>
      <UnorderedList>
        <ListHeading>React admin layout</ListHeading>
        {pages.map(({ name, url, type, childs }) =>
          type === 'link' ? (
            <SidenavLink name={name} url={url} />
          ) : (
            <SidenavDropdown name={name} url={url}>
              {childs.map(child => (
                <SidenavLink name={child.name} url={child.url} />
              ))}
            </SidenavDropdown>
          )
        )}
      </UnorderedList>
    </Nav>
  );
};

Sidenav.propTypes = {
  pages: PropTypes.array,
};

export default Sidenav;
