import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const Nav = styled.nav`
  height: 80px;
  padding: 0 30px;
  background: #75c;
  color: #fff;

  display: flex;
  align-items: center;
  /*
  z-index: 1030;
  height: 64px;
  border: 1px solid red;
  margin: auto;
  padding: auto;
  align-content: center;
  padding: 20px;
  min-height: 64px;
  font-size: 18px;
  max-height: 64px;
  display: flex;

  position: fixed;
  left: 0px;
  border: none;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
  margin-bottom: 23px;
  right: 0;
  top: 0;
  color: white;
  transition: all 0.5s ease;
  &.blue {
    background-color: #2196f3;
  }
  */
  @media (min-width: 768px) {
    left: 150px;
  }
`;

const UnorderedList = styled.ul`
  display: flex;
  list-style: none;
  padding: 0 10px;

  /*

  */
`;

const ListItem = styled.li`
  justify-content: center;
  flex: 1;
  text-decoration: none;
  cursor: pointer;
`;

const Toolbar = props => {
  const { currentPage, username } = props;
  return (
    <Nav className="blue">
      <UnorderedList role="navigation">
        <ListItem>
          <img src="icons/bars.png" alt="bars" />
        </ListItem>
        <ListItem>
          <span>{currentPage || window.location.pathname}</span>
        </ListItem>
        <li style={{ flex: 5 }} />
        <ListItem className="show-admin">
          <img src="icons/users.png" alt="bars" />
          <span>1</span>
        </ListItem>
        <ListItem>
          <strong>{username}</strong>
        </ListItem>
        <ListItem>
          <a href="/api/v1/auth/logout">
            <i className="fa fa-sign-out" /> Logout
          </a>
        </ListItem>
      </UnorderedList>
    </Nav>
  );
};

Toolbar.propTypes = {
  currentPage: PropTypes.string,
  username: PropTypes.string,
};

export default Toolbar;
